import React, { useState, useEffect, useRef } from 'react';
import { auth, db, doc, getDoc } from '../firebase';
import { analyzeTextMood } from '../services/textMoodAnalysisService';
import { analyzeInput } from '../services/inputAnalysisService';
import { generateResponse } from '../services/responseGenerationService';
import { fetchCourseContent } from '../services/courseContentService';
import { getCachedItem, setCachedItem } from '../utils/cacheUtil';

const AICompanion = () => {
  const [userCourse, setUserCourse] = useState('');
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [recommendations, setRecommendations] = useState(null);
  const [showRecommendations, setShowRecommendations] = useState(false);
  const [conversationMemory, setConversationMemory] = useState({
    previousInputs: [],
    previousResponses: [],
    lastInputType: null,
    repetitiveInputCount: 0,
    mentionedTopics: [],
    userMoods: []
  });
  const messagesEndRef = useRef(null);

  // Fetch user's course from Firebase
  useEffect(() => {
    const fetchUserCourse = async () => {
      try {
        const user = auth.currentUser;

        if (!user) {
          console.log('No user logged in');
          return;
        }

        // Get user data from Firestore
        const userDoc = await getDoc(doc(db, "users", user.uid));

        if (userDoc.exists()) {
          const userData = userDoc.data();
          setUserCourse(userData.course || '');
        } else {
          console.log('No user data found');
        }
      } catch (error) {
        console.error('Error fetching user course:', error);
      }
    };

    fetchUserCourse();
  }, []);

  // Scroll to bottom when messages change
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Initial greeting
  useEffect(() => {
    const greetings = [
      "Hi there! I'm your AI learning companion. How are you feeling today?",
      "Hello! I'm your AI study assistant. How's your mood today?",
      "Welcome! I'm here to help with your studies. How are you feeling right now?",
      "Greetings! I'm your AI learning partner. How's your day going?",
      "Hi! I'm your personalized study companion. How are you feeling today?"
    ];

    const randomGreeting = greetings[Math.floor(Math.random() * greetings.length)];

    setMessages([
      {
        type: 'ai',
        text: `${randomGreeting} Tell me about your mood or what's on your mind, and I'll recommend study approaches that might work best for you right now.`
      }
    ]);
  }, []);

  // Handle sending a message
  const handleSendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage = {
      type: 'user',
      text: userInput
    };

    setMessages(prev => [...prev, userMessage]);
    setUserInput('');
    setIsLoading(true);

    try {
      // Advanced input analysis
      const inputAnalysis = analyzeInput(userInput);

      // Traditional mood analysis for backward compatibility
      const moodAnalysis = analyzeTextMood(userInput);

      // Update conversation memory
      setConversationMemory(prevMemory => {
        // Store previous inputs and responses (limited to last 5)
        const updatedInputs = [...prevMemory.previousInputs, userInput].slice(-5);

        // Track mentioned topics
        const updatedTopics = [...new Set([...prevMemory.mentionedTopics, ...inputAnalysis.topics])].slice(-5);

        // Track user moods
        const updatedMoods = [...prevMemory.userMoods, moodAnalysis.mood].slice(-3);

        // Check for repetitive inputs
        const isRepetitive = prevMemory.lastInputType === inputAnalysis.questionTypes[0];
        const repetitiveCount = isRepetitive ? prevMemory.repetitiveInputCount + 1 : 0;

        return {
          previousInputs: updatedInputs,
          previousResponses: prevMemory.previousResponses,
          lastInputType: inputAnalysis.questionTypes[0] || null,
          repetitiveInputCount: repetitiveCount,
          mentionedTopics: updatedTopics,
          userMoods: updatedMoods
        };
      });

      // Fetch content recommendations based on mood and course
      let content = null;

      if (userCourse) {
        try {
          // Create cache key
          const cacheKey = `content_${userCourse}_${moodAnalysis.mood}`;

          // Check cache first
          content = getCachedItem(cacheKey);

          if (!content) {
            // Fetch fresh content
            content = await fetchCourseContent(userCourse, moodAnalysis.mood);

            // Cache the result
            setCachedItem(cacheKey, content);
          }

          setRecommendations(content);
        } catch (error) {
          console.error('Error fetching recommendations:', error);
          // Continue even if recommendations fail
        }
      }

      // Generate AI response using our new service
      setTimeout(() => {
        // Generate response based on input analysis
        const aiResponse = generateResponse(inputAnalysis, conversationMemory, userCourse);

        // Update conversation memory with the new response
        setConversationMemory(prevMemory => {
          const updatedResponses = [...prevMemory.previousResponses, aiResponse].slice(-5);
          return {
            ...prevMemory,
            previousResponses: updatedResponses
          };
        });

        setMessages(prev => [...prev, {
          type: 'ai',
          text: aiResponse
        }]);

        setIsLoading(false);
        if (content) {
          setShowRecommendations(true);
        }
      }, 1000);
    } catch (error) {
      console.error('Error in message processing:', error);

      // Fallback response for error cases
      setTimeout(() => {
        const fallbackResponses = [
          "I'm having trouble understanding right now. Could you rephrase that?",
          "Something went wrong on my end. Could you try asking in a different way?",
          "I apologize, but I'm having difficulty processing that. Could you be more specific?",
          "I seem to be having a technical issue. Could you try a different question?",
          "I'm sorry, but I couldn't quite follow that. Could you elaborate?"
        ];

        const fallbackResponse = fallbackResponses[Math.floor(Math.random() * fallbackResponses.length)];

        setMessages(prev => [...prev, {
          type: 'ai',
          text: fallbackResponse
        }]);

        setIsLoading(false);
      }, 1000);
    }
  };

  // Generate AI response based on mood analysis
  const generateResponse = (analysis, course, context) => {
    const { mood, description, recommendedApproaches } = analysis;

    // Get a random response template based on mood
    const getRandomResponse = (templates) => {
      const randomIndex = Math.floor(Math.random() * templates.length);
      return templates[randomIndex];
    };

    // Response templates for different moods
    const responseTemplates = {
      sad: [
        `I'm sorry to hear that you're feeling down. ${description}`,
        `It sounds like you're having a tough time. When feeling sad, ${description}`,
        `I understand that sadness can make studying difficult. ${description}`
      ],
      angry: [
        `I can sense some frustration in your message. ${description}`,
        `It seems like you might be feeling annoyed or angry. ${description}`,
        `When we're feeling frustrated, ${description}`
      ],
      fearful: [
        `I notice you might be feeling anxious or worried. ${description}`,
        `It sounds like you might be experiencing some anxiety. ${description}`,
        `When feeling uncertain or fearful, ${description}`
      ],
      disgusted: [
        `I sense you're feeling averse to something. ${description}`,
        `It seems like something might be bothering you. ${description}`,
        `When feeling put off by something, ${description}`
      ],
      happy: [
        `That's wonderful! I'm glad you're feeling good. ${description}`,
        `It's great to hear you're in a positive mood! ${description}`,
        `Your positive energy is perfect for learning. ${description}`
      ],
      excited: [
        `I can feel your enthusiasm! ${description}`,
        `Your excitement is contagious! ${description}`,
        `That's great! Your excited state is perfect for tackling challenging material. ${description}`
      ],
      content: [
        `It's nice to hear you're feeling content. ${description}`,
        `A content state of mind is excellent for thoughtful learning. ${description}`,
        `Being content creates a great foundation for learning. ${description}`
      ],
      surprised: [
        `Oh! Something unexpected on your mind? ${description}`,
        `Surprise can lead to new insights! ${description}`,
        `Interesting! Surprise often opens us to new perspectives. ${description}`
      ],
      confused: [
        `I understand confusion can be frustrating. ${description}`,
        `When we're confused, it helps to go back to basics. ${description}`,
        `Confusion is often a step toward deeper understanding. ${description}`
      ],
      stressed: [
        `I can tell you might be feeling overwhelmed. ${description}`,
        `Stress can make learning challenging. ${description}`,
        `When we're stressed, it helps to break things down into smaller steps. ${description}`
      ],
      neutral: [
        `I sense you're in a balanced state of mind. ${description}`,
        `Your neutral mood is actually great for analytical thinking. ${description}`,
        `A neutral state can be perfect for objective learning. ${description}`
      ],
      curious: [
        `Your curiosity is a powerful learning tool! ${description}`,
        `I love your inquisitive mindset! ${description}`,
        `Curiosity drives discovery and learning. ${description}`
      ],
      motivated: [
        `I can sense your motivation! ${description}`,
        `Your drive to learn is inspiring! ${description}`,
        `That motivated mindset will take you far! ${description}`
      ]
    };

    // Check for repetitive responses
    if (context && context.repetitiveResponseCount > 1) {
      // If we've given similar responses multiple times, change the approach
      const varietyResponses = [
        `I notice we've been talking about similar moods. Let's try a different approach.`,
        `Let's explore something new this time.`,
        `I'd like to offer a fresh perspective on your learning.`
      ];

      let response = `${getRandomResponse(varietyResponses)} `;

      // Suggest a completely different learning approach
      const learningApproaches = [
        "try a completely different learning environment",
        "consider taking a short break before returning to your studies",
        "experiment with a new study technique like the Pomodoro method",
        "try explaining the concepts to someone else or even to an imaginary student",
        "create visual maps or diagrams of the material",
        "record yourself explaining difficult concepts and listen back",
        "find real-world applications for what you're learning"
      ];

      response += `One idea might be to ${learningApproaches[Math.floor(Math.random() * learningApproaches.length)]}. `;

      // Add course-specific information if available
      if (course) {
        response += `Would you like me to suggest some specific ${course} resources that align with this approach?`;
      } else {
        response += `Would you like to tell me more about what you're studying so I can provide more specific suggestions?`;
      }

      return response;
    }

    // Check for mood shifts
    if (context && context.previousMoods.length > 1 &&
        context.previousMoods[context.previousMoods.length - 1] !==
        context.previousMoods[context.previousMoods.length - 2]) {

      const previousMood = context.previousMoods[context.previousMoods.length - 2];
      const currentMood = mood;

      // If there's been a significant mood shift
      if ((previousMood === 'sad' || previousMood === 'angry' || previousMood === 'fearful') &&
          (currentMood === 'happy' || currentMood === 'excited' || currentMood === 'content')) {
        // Positive shift
        const positiveShiftResponses = [
          `I'm glad to see your mood has improved! This positive shift can be great for learning.`,
          `It's wonderful to see you're feeling better now. This change in mood can open up new learning opportunities.`,
          `That's a positive change in your mood! Let's take advantage of this energy.`
        ];
        let response = `${getRandomResponse(positiveShiftResponses)} `;

        // Add specific learning approach recommendations
        if (recommendedApproaches && recommendedApproaches.length > 0) {
          const approach = recommendedApproaches[Math.floor(Math.random() * recommendedApproaches.length)];
          response += `With this positive energy, you might find it effective to ${approach.toLowerCase()}. `;
        }

        // Add course-specific information
        if (course) {
          response += `I've selected some ${course} resources that can help you maintain this positive momentum.`;
        } else {
          response += 'Would you like some specific study suggestions that work well with your current positive state?';
        }

        return response;
      }
      if ((previousMood === 'happy' || previousMood === 'excited' || previousMood === 'content') &&
          (currentMood === 'sad' || currentMood === 'angry' || currentMood === 'fearful')) {
        // Negative shift
        const negativeShiftResponses = [
          "I notice your mood seems to have shifted. That's completely normal and happens to everyone.",
          "It seems like your feelings have changed since we last talked. Learning can be affected by these shifts.",
          "I can sense a change in your mood. Let's adjust our approach accordingly."
        ];
        let response = `${getRandomResponse(negativeShiftResponses)} `;

        // Add gentle suggestions
        const gentleApproaches = [
          "take a short break if you need one",
          "be kind to yourself during challenging moments",
          "break your work into smaller, more manageable pieces",
          "focus on just one small task at a time",
          "remember that it's okay to progress slowly sometimes"
        ];

        response += `When your mood changes like this, it can help to ${gentleApproaches[Math.floor(Math.random() * gentleApproaches.length)]}. `;

        // Add course-specific information
        if (course) {
          response += `I have some gentle ${course} resources that might be more suitable right now.`;
        } else {
          response += `Would you like to share what you're working on? I might be able to suggest some specific approaches.`;
        }

        return response;
      }
    }

    // Reference topics if they've been mentioned before
    let topicReference = '';
    if (context?.topicsFocused?.length > 0) {
      const recentTopic = context.topicsFocused[context.topicsFocused.length - 1];
      if (recentTopic) {
        const topicReferences = [
          `Regarding your interest in ${recentTopic}, `,
          `Since you mentioned ${recentTopic} earlier, `,
          `Thinking about ${recentTopic}, `
        ];
        topicReference = getRandomResponse(topicReferences);
      }
    }

    // Standard response flow
    const templates = responseTemplates[mood] || responseTemplates.neutral;
    let response = `${getRandomResponse(templates)} `;

    // Add topic reference if available
    if (topicReference) {
      response += topicReference;
    }

    // Add specific learning approach recommendations
    if (recommendedApproaches && recommendedApproaches.length > 0) {
      const approach = recommendedApproaches[Math.floor(Math.random() * recommendedApproaches.length)];
      response += `One effective approach might be to ${approach.toLowerCase()}. `;
    }

    // Add course-specific information if available
    if (course) {
      const courseResponses = [
        `Since you're studying ${course}, I've prepared some learning resources that might work well with your current mood.`,
        `For your ${course} studies, I've found some materials that align with your current learning style.`,
        `I've selected some ${course} resources that complement how you're feeling right now.`
      ];
      response += getRandomResponse(courseResponses);
    } else {
      const generalResponses = [
        `I'd recommend focusing on ${analysis.learningStyle.toLowerCase()} right now. Would you like some specific study suggestions?`,
        `Given your current state, ${analysis.learningStyle.toLowerCase()} might be most effective. Would you like to explore this approach?`,
        `This might be a good time for ${analysis.learningStyle.toLowerCase()}. Would you like me to elaborate on this?`
      ];
      response += getRandomResponse(generalResponses);
    }

    return response;
  };

  // Handle key press (Enter to send)
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="bg-[#1F2833] rounded-lg overflow-hidden flex flex-col h-[600px]">
      {/* Header */}
      <div className="bg-[#0B0C10] p-4 border-b border-[#45A29E]">
        <h3 className="text-[#66FCF1] font-medium">AI Learning Companion</h3>
        <p className="text-[#CFC6C7] text-sm">
          Share how you're feeling, and I'll recommend study approaches
        </p>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div
            key={`message-${index}-${message.type}`}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-[80%] rounded-lg p-3 ${
                message.type === 'user'
                  ? 'bg-[#45A29E] text-white'
                  : 'bg-[#0B0C10] text-[#CFC6C7]'
              }`}
            >
              {message.text}
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-[#0B0C10] text-[#CFC6C7] rounded-lg p-3 max-w-[80%]">
              <div className="flex space-x-2">
                <div className="w-2 h-2 rounded-full bg-[#66FCF1] animate-bounce" />
                <div className="w-2 h-2 rounded-full bg-[#66FCF1] animate-bounce" style={{ animationDelay: '0.2s' }} />
                <div className="w-2 h-2 rounded-full bg-[#66FCF1] animate-bounce" style={{ animationDelay: '0.4s' }} />
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Recommendations Section */}
      {showRecommendations && recommendations && (
        <div className="bg-[#0B0C10] p-4 border-t border-[#45A29E] max-h-[250px] overflow-y-auto">
          <h4 className="text-[#66FCF1] font-medium mb-3">Recommended Resources</h4>

          {/* Videos */}
          {recommendations.videos && recommendations.videos.length > 0 && (
            <div className="mb-3">
              <h5 className="text-[#CFC6C7] text-sm font-medium mb-2">Videos for your current mood:</h5>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
                {recommendations.videos.slice(0, 2).map((video) => (
                  <a
                    key={`video-${video.id}`}
                    href={video.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center bg-[#1F2833] rounded p-2 hover:bg-[#1F2833]/80 transition-colors"
                  >
                    <div className="w-16 h-12 flex-shrink-0 mr-2">
                      <img
                        src={video.thumbnail}
                        alt={video.title}
                        className="w-full h-full object-cover rounded"
                      />
                    </div>
                    <div className="flex-1">
                      <p className="text-[#CFC6C7] text-xs line-clamp-2">{video.title}</p>
                    </div>
                  </a>
                ))}
              </div>
            </div>
          )}

          {/* Resources */}
          {recommendations.resources && recommendations.resources.length > 0 && (
            <div>
              <h5 className="text-[#CFC6C7] text-sm font-medium mb-2">Learning resources:</h5>
              <div className="space-y-2">
                {recommendations.resources.slice(0, 2).map((resource, index) => (
                  <a
                    key={`resource-${resource.source}-${index}`}
                    href={resource.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block bg-[#1F2833] rounded p-2 hover:bg-[#1F2833]/80 transition-colors"
                  >
                    <p className="text-[#CFC6C7] text-xs font-medium">{resource.title}</p>
                    <p className="text-gray-400 text-xs mt-1">{resource.source}</p>
                  </a>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Input */}
      <div className="p-4 border-t border-[#45A29E]">
        <div className="flex">
          <textarea
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Tell me how you're feeling today..."
            className="flex-1 bg-[#0B0C10] text-[#CFC6C7] rounded-l-lg p-3 focus:outline-none resize-none"
            rows="2"
          />
          <button
            type="button"
            onClick={handleSendMessage}
            disabled={isLoading || !userInput.trim()}
            className="bg-[#66FCF1] text-[#0B0C10] px-4 rounded-r-lg font-medium hover:bg-[#45A29E] transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default AICompanion;
