import React, { useState, useEffect, useRef } from 'react';
import { auth, db, doc, getDoc } from '../firebase';
import { analyzeTextMood } from '../services/textMoodAnalysisService';
import { fetchCourseContent } from '../services/courseContentService';
import { getCachedItem, setCachedItem } from '../utils/cacheUtil';

const AICompanion = () => {
  const [userCourse, setUserCourse] = useState('');
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [moodAnalysis, setMoodAnalysis] = useState(null);
  const [recommendations, setRecommendations] = useState(null);
  const [showRecommendations, setShowRecommendations] = useState(false);
  const messagesEndRef = useRef(null);

  // Fetch user's course from Firebase
  useEffect(() => {
    const fetchUserCourse = async () => {
      try {
        const user = auth.currentUser;

        if (!user) {
          console.log('No user logged in');
          return;
        }

        // Get user data from Firestore
        const userDoc = await getDoc(doc(db, "users", user.uid));

        if (userDoc.exists()) {
          const userData = userDoc.data();
          setUserCourse(userData.course || '');
        } else {
          console.log('No user data found');
        }
      } catch (error) {
        console.error('Error fetching user course:', error);
      }
    };

    fetchUserCourse();
  }, []);

  // Scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Initial greeting
  useEffect(() => {
    setMessages([
      {
        type: 'ai',
        text: "Hi there! I'm your AI learning companion. How are you feeling today? Tell me about your mood or what's on your mind, and I'll recommend study approaches that might work best for you right now."
      }
    ]);
  }, []);

  // Handle sending a message
  const handleSendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage = {
      type: 'user',
      text: userInput
    };

    setMessages(prev => [...prev, userMessage]);
    setUserInput('');
    setIsLoading(true);

    // Analyze the user's mood from their message
    const analysis = analyzeTextMood(userInput);
    setMoodAnalysis(analysis);

    // Fetch content recommendations based on mood and course
    if (userCourse) {
      try {
        // Create cache key
        const cacheKey = `content_${userCourse}_${analysis.mood}`;

        // Check cache first
        let content = getCachedItem(cacheKey);

        if (!content) {
          // Fetch fresh content
          content = await fetchCourseContent(userCourse, analysis.mood);

          // Cache the result
          setCachedItem(cacheKey, content);
        }

        setRecommendations(content);
      } catch (error) {
        console.error('Error fetching recommendations:', error);
      }
    }

    // Generate AI response based on mood analysis
    setTimeout(() => {
      const aiResponse = generateResponse(analysis, userCourse);

      setMessages(prev => [...prev, {
        type: 'ai',
        text: aiResponse
      }]);

      setIsLoading(false);
      setShowRecommendations(true);
    }, 1000);
  };

  // Generate AI response based on mood analysis
  const generateResponse = (analysis, course) => {
    const { mood, description } = analysis;

    let response = `I sense that you're feeling ${mood}. ${description} `;

    if (course) {
      response += `Since you're studying ${course}, I've prepared some learning resources that might work well with your current mood.`;
    } else {
      response += `I'd recommend focusing on ${analysis.learningStyle.toLowerCase()} right now. Would you like some specific study suggestions?`;
    }

    return response;
  };

  // Handle key press (Enter to send)
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="bg-[#1F2833] rounded-lg overflow-hidden flex flex-col h-[600px]">
      {/* Header */}
      <div className="bg-[#0B0C10] p-4 border-b border-[#45A29E]">
        <h3 className="text-[#66FCF1] font-medium">AI Learning Companion</h3>
        <p className="text-[#CFC6C7] text-sm">
          Share how you're feeling, and I'll recommend study approaches
        </p>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div 
            key={index} 
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div 
              className={`max-w-[80%] rounded-lg p-3 ${
                message.type === 'user' 
                  ? 'bg-[#45A29E] text-white' 
                  : 'bg-[#0B0C10] text-[#CFC6C7]'
              }`}
            >
              {message.text}
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-[#0B0C10] text-[#CFC6C7] rounded-lg p-3 max-w-[80%]">
              <div className="flex space-x-2">
                <div className="w-2 h-2 rounded-full bg-[#66FCF1] animate-bounce"></div>
                <div className="w-2 h-2 rounded-full bg-[#66FCF1] animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-2 h-2 rounded-full bg-[#66FCF1] animate-bounce" style={{ animationDelay: '0.4s' }}></div>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Recommendations Section */}
      {showRecommendations && recommendations && (
        <div className="bg-[#0B0C10] p-4 border-t border-[#45A29E] max-h-[250px] overflow-y-auto">
          <h4 className="text-[#66FCF1] font-medium mb-3">Recommended Resources</h4>

          {/* Videos */}
          {recommendations.videos && recommendations.videos.length > 0 && (
            <div className="mb-3">
              <h5 className="text-[#CFC6C7] text-sm font-medium mb-2">Videos for your current mood:</h5>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
                {recommendations.videos.slice(0, 2).map((video, index) => (
                  <a 
                    key={index}
                    href={video.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center bg-[#1F2833] rounded p-2 hover:bg-[#1F2833]/80 transition-colors"
                  >
                    <div className="w-16 h-12 flex-shrink-0 mr-2">
                      <img 
                        src={video.thumbnail} 
                        alt={video.title}
                        className="w-full h-full object-cover rounded"
                      />
                    </div>
                    <div className="flex-1">
                      <p className="text-[#CFC6C7] text-xs line-clamp-2">{video.title}</p>
                    </div>
                  </a>
                ))}
              </div>
            </div>
          )}

          {/* Resources */}
          {recommendations.resources && recommendations.resources.length > 0 && (
            <div>
              <h5 className="text-[#CFC6C7] text-sm font-medium mb-2">Learning resources:</h5>
              <div className="space-y-2">
                {recommendations.resources.slice(0, 2).map((resource, index) => (
                  <a
                    key={index}
                    href={resource.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block bg-[#1F2833] rounded p-2 hover:bg-[#1F2833]/80 transition-colors"
                  >
                    <p className="text-[#CFC6C7] text-xs font-medium">{resource.title}</p>
                    <p className="text-gray-400 text-xs mt-1">{resource.source}</p>
                  </a>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Input */}
      <div className="p-4 border-t border-[#45A29E]">
        <div className="flex">
          <textarea
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Tell me how you're feeling today..."
            className="flex-1 bg-[#0B0C10] text-[#CFC6C7] rounded-l-lg p-3 focus:outline-none resize-none"
            rows="2"
          />
          <button
            onClick={handleSendMessage}
            disabled={isLoading || !userInput.trim()}
            className="bg-[#66FCF1] text-[#0B0C10] px-4 rounded-r-lg font-medium hover:bg-[#45A29E] transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default AICompanion;
