import React, { useState } from 'react';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { 
      text: "Hi there! I'm your AI companion. How can I help you today?", 
      sender: 'bot' 
    }
  ]);
  const [input, setInput] = useState('');

  const handleSendMessage = (e) => {
    e.preventDefault();

    if (!input.trim()) return;

    // Add user message
    const userMessage = { text: input, sender: 'user' };
    setMessages([...messages, userMessage]);
    setInput('');

    // Simulate bot response
    setTimeout(() => {
      const botResponses = [
        "I understand how you're feeling. Would you like to talk more about it?",
        "That sounds challenging. What do you think might help in this situation?",
        "I'm here to listen. Feel free to share more if you'd like.",
        "Thank you for sharing that with me. How does that make you feel?",
        "It's completely normal to feel that way. What would help you feel better right now?"
      ];

      const randomResponse = botResponses[Math.floor(Math.random() * botResponses.length)];
      setMessages(prev => [...prev, { text: randomResponse, sender: 'bot' }]);
    }, 1000);
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold text-[#66FCF1] mb-6">ðŸ¤– AI Companion</h1>

      <div className="bg-[#1F2833] rounded-xl shadow-lg overflow-hidden flex flex-col h-[70vh]">
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div 
              key={index} 
              className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div 
                className={`max-w-[80%] rounded-xl p-3 ${
                  message.sender === 'user' 
                    ? 'bg-[#45A29E] text-white' 
                    : 'bg-[#0B0C10] text-[#CFC6C7]'
                }`}
              >
                {message.text}
              </div>
            </div>
          ))}
        </div>

        <form onSubmit={handleSendMessage} className="p-4 border-t border-[#45A29E] bg-[#0B0C10]">
          <div className="flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className="flex-1 bg-[#1F2833] text-[#CFC6C7] p-3 rounded-l-lg focus:outline-none"
            />
            <button 
              type="submit"
              className="bg-[#66FCF1] text-[#0B0C10] px-4 rounded-r-lg font-semibold"
            >
              Send
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Chatbot;
